import React, { Component } from "react";
import ProjectList from "../projects/projectlist";
import { connect } from "react-redux";
import { useFirestore, firestoreConnect } from "react-redux-firebase";
import { compose } from "redux";
import { Redirect } from "react-router-dom";

const getProject = (firestore) => {
	return (projID) => {
		return firestore
			.collection("projects")
			.doc(projID)
			.get()
			.then((doc) => {
				return doc.data();
			})
			.catch((err) => {
				console.log("error with project firestore", err);
			});
	};
};

const getMyList = (uid, firestore) => {
	return firestore
		.collection("users")
		.doc(uid)
		.get()
		.then((doc) => {
			return doc.data().projects;
		})
		.catch((err) => {
			console.log("Error with user firestore", err);
		});
};

const fetchMine = (uid, firestore) => {
	const myproj = getMyList(uid, firestore); // list of project id's
	return myproj.map(getProject(firestore));
};

class MyProjects extends Component {
	render() {
		const { auth, userFirestore } = this.props;
		const firestore = useFirestore();
		const projects = fetchMine(auth.uid, firestore);
		// TODO: change const projects so its only this user's projects
		// Route Protection
		if (!auth.uid) return <Redirect to="/signin" />;
		// otws good to go
		return (
			<div>
				<span style={{ textAlign: "center" }}>
					<h3> You got some awesome projects! </h3>
				</span>
				<div className="dashboard container">
					<div className="row">
						<div className="col s16 m10">
							<ProjectList projects={projects} />
						</div>
					</div>
				</div>
			</div>
		);
	}
}

const mapStateToProps = (state) => {
	console.log(state);
	return {
		projects: state.firestore.ordered.projects,
		auth: state.firebase.auth
	};
};

export default compose(
	connect(mapStateToProps),
	firestoreConnect([
		{ collection: "projects", orderBy: ["createdAt", "desc"] }
	])
)(MyProjects);
